package com.marusys.downloadsdk
/** * Interface for download operations and state management */ interface DownloadController { fun pauseDownload() fun resumeDownload() fun cancelDownload() fun getDownloadState(): Int fun getCurrentProgress(): Long fun getTotalSize(): Long }
/** * Listener interface for download events */ interface DownloadListener { fun onProgressUpdate(downloadId: Long, downloaded: Long, total: Long, percentage: Int) fun onDownloadComplete(downloadId: Long, success: Boolean, filePath: String?) fun onDownloadError(downloadId: Long, error: String) fun onDownloadStateChanged(downloadId: Long, state: Int) fun onDownloadPaused(downloadId: Long) fun onDownloadResumed(downloadId: Long) fun onDownloadCancelled(downloadId: Long) fun onNetworkReconnected(downloadId: Long) }

package com.marusys.downloadsdk

import android.content.Context
import android.util.Log
import com.marusys.downloadsdk.model.DownloadRequestFileModel
import java.io.File
import java.util.concurrent.ConcurrentHashMap

/**
 * Main Download Manager that handles multiple concurrent downloads
 * with pause, resume, cancel, and network reconnection support
 */
class DownloadManager private constructor() {

    private val TAG = "DownloadManager"
    private val activeDownloads = ConcurrentHashMap<Long, DownloadTaskManager>()

    companion object {
        @Volatile
        private var INSTANCE: DownloadManager? = null

        fun getInstance(): DownloadManager {
            return INSTANCE ?: synchronized(this) {
                INSTANCE ?: DownloadManager().also { INSTANCE = it }
            }
        }
    }

    /**
     * Start a new download
     */
    fun startDownload(
        context: Context,
        downloadRequest: DownloadRequestFileModel,
        outputFile: File,
        listener: DownloadListener? = null
    ): Long {
        val downloadId = downloadRequest.downloadId

        // Check if download is already active
        if (isDownloadActive(downloadId)) {
            Log.w(TAG, "Download with ID $downloadId is already active")
            return downloadId
        }

        // Create and start download task
        val downloadTask = DownloadTaskManager(context, downloadRequest, outputFile, listener)
        activeDownloads[downloadId] = downloadTask
        downloadTask.startDownload()

        Log.d(TAG, "Started download: ${downloadRequest.fileName} (ID: $downloadId)")
        return downloadId
    }

    /**
     * Pause a download
     */
    fun pauseDownload(downloadId: Long): Boolean {
        return activeDownloads[downloadId]?.let { task ->
            task.pauseDownload()
            Log.d(TAG, "Paused download ID: $downloadId")
            true
        } ?: false
    }

    /**
     * Resume a paused download
     */
    fun resumeDownload(downloadId: Long): Boolean {
        return activeDownloads[downloadId]?.let { task ->
            task.resumeDownload()
            Log.d(TAG, "Resumed download ID: $downloadId")
            true
        } ?: false
    }

    /**
     * Cancel a download
     */
    fun cancelDownload(downloadId: Long): Boolean {
        return activeDownloads[downloadId]?.let { task ->
            task.cancelDownload()
            activeDownloads.remove(downloadId)
            Log.d(TAG, "Cancelled download ID: $downloadId")
            true
        } ?: false
    }

    /**
     * Cancel all active downloads
     */
    fun cancelAllDownloads() {
        val downloadIds = activeDownloads.keys.toList()
        downloadIds.forEach { downloadId ->
            cancelDownload(downloadId)
        }
        Log.d(TAG, "Cancelled all downloads (${downloadIds.size} downloads)")
    }

    /**
     * Get download state
     */
    fun getDownloadState(downloadId: Long): Int? {
        return activeDownloads[downloadId]?.getDownloadState()
    }

    /**
     * Get current progress in bytes
     */
    fun getDownloadProgress(downloadId: Long): Long {
        return activeDownloads[downloadId]?.getCurrentProgress() ?: 0L
    }

    /**
     * Get total file size in bytes
     */
    fun getDownloadTotalSize(downloadId: Long): Long {
        return activeDownloads[downloadId]?.getTotalSize() ?: 0L
    }

    /**
     * Get complete download information
     */
    fun getDownloadInfo(downloadId: Long): DownloadRequestFileModel? {
        return activeDownloads[downloadId]?.getDownloadInfo()
    }

    /**
     * Check if download is active
     */
    fun isDownloadActive(downloadId: Long): Boolean {
        return activeDownloads[downloadId]?.isActive() == true
    }

    /**
     * Get all active download IDs
     */
    fun getActiveDownloadIds(): Set<Long> {
        return activeDownloads.keys.toSet()
    }

    /**
     * Get number of active downloads
     */
    fun getActiveDownloadCount(): Int = activeDownloads.size

    /**
     * Pause all active downloads
     */
    fun pauseAllDownloads() {
        activeDownloads.values.forEach { task ->
            task.pauseDownload()
        }
        Log.d(TAG, "Paused all downloads (${activeDownloads.size} downloads)")
    }

    /**
     * Resume all paused downloads
     */
    fun resumeAllDownloads() {
        activeDownloads.values.forEach { task ->
            if (task.getDownloadState() == Constants.STATE_PAUSED) {
                task.resumeDownload()
            }
        }
        Log.d(TAG, "Resumed all paused downloads")
    }

    /**
     * Clean up completed or failed downloads from active list
     */
    fun cleanupCompletedDownloads() {
        val completedIds = mutableListOf<Long>()

        activeDownloads.forEach { (id, task) ->
            val state = task.getDownloadState()
            if (state in listOf(Constants.STATE_COMPLETED, Constants.STATE_FAILED, Constants.STATE_CANCELLED)) {
                task.cleanup()
                completedIds.add(id)
            }
        }

        completedIds.forEach { id ->
            activeDownloads.remove(id)
        }

        if (completedIds.isNotEmpty()) {
            Log.d(TAG, "Cleaned up ${completedIds.size} completed downloads")
        }
    }

    /**
     * Get download statistics
     */
    fun getDownloadStatistics(): DownloadStatistics {
        var totalDownloading = 0
        var totalPaused = 0
        var totalWaitingForNetwork = 0
        var totalCompleted = 0
        var totalFailed = 0

        activeDownloads.values.forEach { task ->
            when (task.getDownloadState()) {
                Constants.STATE_DOWNLOADING -> totalDownloading++
                Constants.STATE_PAUSED -> totalPaused++
                Constants.STATE_WAITING_FOR_NETWORK -> totalWaitingForNetwork++
                Constants.STATE_COMPLETED -> totalCompleted++
                Constants.STATE_FAILED -> totalFailed++
            }
        }

        return DownloadStatistics(
            totalActive = activeDownloads.size,
            downloading = totalDownloading,
            paused = totalPaused,
            waitingForNetwork = totalWaitingForNetwork,
            completed = totalCompleted,
            failed = totalFailed
        )
    }
}

/**
 * Data class for download statistics
 */
data class DownloadStatistics(
    val totalActive: Int,
    val downloading: Int,
    val paused: Int,
    val waitingForNetwork: Int,
    val completed: Int,
    val failed: Int
)

package com.marusys.downloadsdk

import android.content.Context
import android.util.Log
import com.marusys.downloadsdk.db.DownloadPersistenceManager
import com.marusys.downloadsdk.model.DownloadRequestFileModel
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.launch
import java.io.File
import java.util.concurrent.ConcurrentHashMap

/**
 * Enhanced Download Manager with Room database persistence
 * Automatically resumes downloads when app is reopened
 */
class DownloadManager private constructor() {

    private val TAG = "DownloadManager"
    private val activeDownloads = ConcurrentHashMap<Long, DownloadTaskManager>()
    private val persistenceManager = DownloadPersistenceManager.getInstance()
    private val resumeScope = CoroutineScope(Dispatchers.IO)

    companion object {
        @Volatile
        private var INSTANCE: DownloadManager? = null

        fun getInstance(): DownloadManager {
            return INSTANCE ?: synchronized(this) {
                INSTANCE ?: DownloadManager().also { INSTANCE = it }
            }
        }
    }

    /**
     * Initialize the download manager and resume incomplete downloads
     */
    fun initialize(context: Context, autoResumeIncompleteDownloads: Boolean = true) {
        persistenceManager.initialize(context)

        if (autoResumeIncompleteDownloads) {
            resumeIncompleteDownloads(context)
        }

        Log.d(TAG, "DownloadManager initialized")
    }

    /**
     * Resume all incomplete downloads from database when app opens
     */
    fun resumeIncompleteDownloads(context: Context) {
        resumeScope.launch {
            try {
                val incompleteDownloads = persistenceManager.getIncompleteDownloads()
                Log.d(TAG, "Resuming ${incompleteDownloads.size} incomplete downloads")

                incompleteDownloads.forEach { request ->
                    // Validate that the file still exists and has correct size
                    if (persistenceManager.validateDownloadFile(request.downloadId)) {
                        val outputFile = File(context.getExternalFilesDir(null), request.fileName)
                        resumeDownloadFromDatabase(context, request, outputFile)
                    } else {
                        Log.w(TAG, "Cannot resume download ${request.fileName} - file validation failed")
                        // Reset download progress in database
                        persistenceManager.updateProgress(request.downloadId, 0L, request.totalBytes)
                        persistenceManager.updateState(request.downloadId, Constants.STATE_IDLE)
                    }
                }
            } catch (e: Exception) {
                Log.e(TAG, "Error resuming incomplete downloads", e)
            }
        }
    }

    private fun resumeDownloadFromDatabase(
        context: Context,
        request: DownloadRequestFileModel,
        outputFile: File
    ) {
        Log.d(TAG, "Resuming download: ${request.fileName} from ${formatBytes(request.downloadedBytes)}")

        val listener = DatabaseIntegratedListener(request.downloadId)
        val downloadTask = DownloadTaskManager(context, request, outputFile, listener)
        activeDownloads[request.downloadId] = downloadTask

        // Set initial state to paused so user can choose to resume
        persistenceManager.updateState(request.downloadId, Constants.STATE_PAUSED)

        Log.d(TAG, "Download ${request.fileName} ready to resume (currently paused)")
    }

    /**
     * Start a new download with database persistence
     */
    fun startDownload(
        context: Context,
        downloadRequest: DownloadRequestFileModel,
        outputFile: File,
        listener: DownloadListener? = null
    ): Long {
        val downloadId = downloadRequest.downloadId

        // Check if download is already active
        if (isDownloadActive(downloadId)) {
            Log.w(TAG, "Download with ID $downloadId is already active")
            return downloadId
        }

        // Save to database
        resumeScope.launch {
            persistenceManager.saveDownloadRequest(downloadRequest, outputFile.absolutePath)
        }

        // Create composite listener that includes database updates
        val compositeListener = CompositeDownloadListener(downloadId, listener)

        // Create and start download task
        val downloadTask = DownloadTaskManager(context, downloadRequest, outputFile, compositeListener)
        activeDownloads[downloadId] = downloadTask
        downloadTask.startDownload()

        Log.d(TAG, "Started download: ${downloadRequest.fileName} (ID: $downloadId)")
        return downloadId
    }

    /**
     * Pause a download
     */
    fun pauseDownload(downloadId: Long): Boolean {
        return activeDownloads[downloadId]?.let { task ->
            task.pauseDownload()
            persistenceManager.updateState(downloadId, Constants.STATE_PAUSED)
            Log.d(TAG, "Paused download ID: $downloadId")
            true
        } ?: false
    }

    /**
     * Resume a paused download
     */
    fun resumeDownload(downloadId: Long): Boolean {
        return activeDownloads[downloadId]?.let { task ->
            task.resumeDownload()
            persistenceManager.updateState(downloadId, Constants.STATE_DOWNLOADING)
            Log.d(TAG, "Resumed download ID: $downloadId")
            true
        } ?: false
    }

    /**
     * Cancel a download
     */
    fun cancelDownload(downloadId: Long): Boolean {
        return activeDownloads[downloadId]?.let { task ->
            task.cancelDownload()
            persistenceManager.updateState(downloadId, Constants.STATE_CANCELLED)
            activeDownloads.remove(downloadId)
            Log.d(TAG, "Cancelled download ID: $downloadId")
            true
        } ?: false
    }

    /**
     * Cancel all active downloads
     */
    fun cancelAllDownloads() {
        val downloadIds = activeDownloads.keys.toList()
        downloadIds.forEach { downloadId ->
            cancelDownload(downloadId)
        }
        Log.d(TAG, "Cancelled all downloads (${downloadIds.size} downloads)")
    }

    /**
     * Get download state
     */
    fun getDownloadState(downloadId: Long): Int? {
        return activeDownloads[downloadId]?.getDownloadState()
    }

    /**
     * Get current progress in bytes
     */
    fun getDownloadProgress(downloadId: Long): Long {
        return activeDownloads[downloadId]?.getCurrentProgress() ?: 0L
    }

    /**
     * Get total file size in bytes
     */
    fun getDownloadTotalSize(downloadId: Long): Long {
        return activeDownloads[downloadId]?.getTotalSize() ?: 0L
    }

    /**
     * Get complete download information
     */
    fun getDownloadInfo(downloadId: Long): DownloadRequestFileModel? {
        return activeDownloads[downloadId]?.getDownloadInfo()
    }

    /**
     * Check if download is active
     */
    fun isDownloadActive(downloadId: Long): Boolean {
        return activeDownloads[downloadId]?.isActive() == true
    }

    /**
     * Get all active download IDs
     */
    fun getActiveDownloadIds(): Set<Long> {
        return activeDownloads.keys.toSet()
    }

    /**
     * Get number of active downloads
     */
    fun getActiveDownloadCount(): Int = activeDownloads.size

    /**
     * Pause all active downloads
     */
    fun pauseAllDownloads() {
        activeDownloads.values.forEach { task ->
            task.pauseDownload()
        }
        Log.d(TAG, "Paused all downloads (${activeDownloads.size} downloads)")
    }

    /**
     * Resume all paused downloads
     */
    fun resumeAllDownloads() {
        activeDownloads.values.forEach { task ->
            if (task.getDownloadState() == Constants.STATE_PAUSED) {
                task.resumeDownload()
            }
        }
        Log.d(TAG, "Resumed all paused downloads")
    }

    /**
     * Clean up completed or failed downloads from active list
     */
    fun cleanupCompletedDownloads() {
        val completedIds = mutableListOf<Long>()

        activeDownloads.forEach { (id, task) ->
            val state = task.getDownloadState()
            if (state in listOf(Constants.STATE_COMPLETED, Constants.STATE_FAILED, Constants.STATE_CANCELLED)) {
                task.cleanup()
                completedIds.add(id)
            }
        }

        completedIds.forEach { id ->
            activeDownloads.remove(id)
        }

        if (completedIds.isNotEmpty()) {
            Log.d(TAG, "Cleaned up ${completedIds.size} completed downloads")
        }
    }

    /**
     * Get download statistics
     */
    fun getDownloadStatistics(): DownloadStatistics {
        var totalDownloading = 0
        var totalPaused = 0
        var totalWaitingForNetwork = 0
        var totalCompleted = 0
        var totalFailed = 0

        activeDownloads.values.forEach { task ->
            when (task.getDownloadState()) {
                Constants.STATE_DOWNLOADING -> totalDownloading++
                Constants.STATE_PAUSED -> totalPaused++
                Constants.STATE_WAITING_FOR_NETWORK -> totalWaitingForNetwork++
                Constants.STATE_COMPLETED -> totalCompleted++
                Constants.STATE_FAILED -> totalFailed++
            }
        }

        return DownloadStatistics(
            totalActive = activeDownloads.size,
            downloading = totalDownloading,
            paused = totalPaused,
            waitingForNetwork = totalWaitingForNetwork,
            completed = totalCompleted,
            failed = totalFailed
        )
    }

    private fun formatBytes(bytes: Long): String {
        return when {
            bytes >= 1024 * 1024 * 1024 -> "${bytes / (1024 * 1024 * 1024)} GB"
            bytes >= 1024 * 1024 -> "${bytes / (1024 * 1024)} MB"
            bytes >= 1024 -> "${bytes / 1024} KB"
            else -> "$bytes B"
        }
    }

    /**
     * Composite listener that handles both user callbacks and database persistence
     */
    private inner class CompositeDownloadListener(
        private val downloadId: Long,
        private val userListener: DownloadListener?
    ) : DownloadListener {

        override fun onProgressUpdate(downloadId: Long, downloaded: Long, total: Long, percentage: Int) {
            // Update database
            persistenceManager.updateProgress(downloadId, downloaded, total)
            // Notify user listener
            userListener?.onProgressUpdate(downloadId, downloaded, total, percentage)
        }

        override fun onDownloadComplete(downloadId: Long, success: Boolean, filePath: String?) {
            // Update database
            val state = if (success) Constants.STATE_COMPLETED else Constants.STATE_FAILED
            persistenceManager.updateState(downloadId, state)
            // Remove from active downloads
            activeDownloads.remove(downloadId)
            // Notify user listener
            userListener?.onDownloadComplete(downloadId, success, filePath)
        }

        override fun onDownloadError(downloadId: Long, error: String) {
            // Update database
            persistenceManager.updateState(downloadId, Constants.STATE_FAILED, error)
            // Notify user listener
            userListener?.onDownloadError(downloadId, error)
        }

        override fun onDownloadStateChanged(downloadId: Long, state: Int) {
            // Update database
            persistenceManager.updateState(downloadId, state)
            // Notify user listener
            userListener?.onDownloadStateChanged(downloadId, state)
        }

        override fun onDownloadPaused(downloadId: Long) {
            persistenceManager.updateState(downloadId, Constants.STATE_PAUSED)
            userListener?.onDownloadPaused(downloadId)
        }

        override fun onDownloadResumed(downloadId: Long) {
            persistenceManager.updateState(downloadId, Constants.STATE_DOWNLOADING)
            userListener?.onDownloadResumed(downloadId)
        }

        override fun onDownloadCancelled(downloadId: Long) {
            persistenceManager.updateState(downloadId, Constants.STATE_CANCELLED)
            userListener?.onDownloadCancelled(downloadId)
        }

        override fun onNetworkReconnected(downloadId: Long) {
            userListener?.onNetworkReconnected(downloadId)
        }
    }

    /**
     * Database-only listener for resumed downloads
     */
    private inner class DatabaseIntegratedListener(
        private val downloadId: Long
    ) : DownloadListener {

        override fun onProgressUpdate(downloadId: Long, downloaded: Long, total: Long, percentage: Int) {
            persistenceManager.updateProgress(downloadId, downloaded, total)
        }

        override fun onDownloadComplete(downloadId: Long, success: Boolean, filePath: String?) {
            val state = if (success) Constants.STATE_COMPLETED else Constants.STATE_FAILED
            persistenceManager.updateState(downloadId, state)
            activeDownloads.remove(downloadId)
        }

        override fun onDownloadError(downloadId: Long, error: String) {
            persistenceManager.updateState(downloadId, Constants.STATE_FAILED, error)
        }

        override fun onDownloadStateChanged(downloadId: Long, state: Int) {
            persistenceManager.updateState(downloadId, state)
        }

        override fun onDownloadPaused(downloadId: Long) {
            persistenceManager.updateState(downloadId, Constants.STATE_PAUSED)
        }

        override fun onDownloadResumed(downloadId: Long) {
            persistenceManager.updateState(downloadId, Constants.STATE_DOWNLOADING)
        }

        override fun onDownloadCancelled(downloadId: Long) {
            persistenceManager.updateState(downloadId, Constants.STATE_CANCELLED)
        }

        override fun onNetworkReconnected(downloadId: Long) {
            // Handle network reconnection
        }
    }
}

data class DownloadStatistics(
    val totalActive: Int,
    val downloading: Int,
    val paused: Int,
    val waitingForNetwork: Int,
    val completed: Int,
    val failed: Int
)

package com.marusys.downloadsdk

import android.Manifest
import android.content.Context
import android.util.Log
import androidx.annotation.RequiresPermission
import com.marusys.downloadsdk.model.DownloadRequestFileModel
import kotlinx.coroutines.*
import java.io.File
import java.io.FileOutputStream
import java.io.IOException
import java.util.concurrent.atomic.AtomicBoolean
import java.util.concurrent.atomic.AtomicInteger
import java.util.concurrent.atomic.AtomicLong

class DownloadTaskManager(
    private val context: Context,
    private var downloadRequestFileModel: DownloadRequestFileModel,
    private val outputFile: File,
    private val listener: DownloadListener? = null
) : DownloadController, NetworkManager.NetworkConnectionState {

    private val TAG = "DownloadTaskManager"

    // Core components
    private val scope = CoroutineScope(Dispatchers.IO + SupervisorJob())
    private val httpConnectionManager = HttpConnectionManager(downloadRequestFileModel)
    private val networkManager = NetworkManager(context, this)

    // State management
    private val currentState = AtomicInteger(Constants.STATE_IDLE)
    private val isPaused = AtomicBoolean(false)
    private val isCancelled = AtomicBoolean(false)
    private val isWaitingForNetwork = AtomicBoolean(false)

    // Download progress tracking
    private val downloadedBytes = AtomicLong(downloadRequestFileModel.downloadedBytes)
    private val totalBytes = AtomicLong(downloadRequestFileModel.totalBytes)

    // Jobs and retry management
    private var downloadJob: Job? = null
    private var retryCount = 0

    @RequiresPermission(Manifest.permission.ACCESS_NETWORK_STATE)
    fun startDownload() {
        if (currentState.get() == Constants.STATE_DOWNLOADING) {
            Log.w(TAG, "Download already in progress")
            return
        }

        networkManager.registerNetworkCallback()

        if (!networkManager.isConnected()) {
            setState(Constants.STATE_WAITING_FOR_NETWORK)
            Log.d(TAG, "No network connection, waiting for network...")
            return
        }

        executeDownload()
    }

    private fun executeDownload() {
        if (isCancelled.get()) return

        setState(Constants.STATE_DOWNLOADING)
        isPaused.set(false)

        downloadJob = scope.launch {
            try {
                performDownload()
            } catch (e: CancellationException) {
                Log.d(TAG, "Download cancelled")
                handleDownloadCancellation()
            } catch (e: IOException) {
                Log.e(TAG, "Download IO error", e)
                handleNetworkError(e)
            } catch (e: Exception) {
                Log.e(TAG, "Download error", e)
                handleDownloadError(e)
            }
        }
    }

    private suspend fun performDownload() {
        // Update request model with current progress
        downloadRequestFileModel = downloadRequestFileModel.copy(
            downloadedBytes = downloadedBytes.get()
        )

        // Establish connection
        httpConnectionManager.buildConnectionToDownload()

        val contentLength = httpConnectionManager.getContentLength()
        if (contentLength > 0 && totalBytes.get() <= 0) {
            totalBytes.set(contentLength + downloadedBytes.get())
        }

        // Start streaming download
        httpConnectionManager.getInputStream()?.use { inputStream ->
            FileOutputStream(outputFile, downloadedBytes.get() > 0).use { outputStream ->
                downloadDataStream(inputStream, outputStream)
            }
        } ?: throw IOException("Unable to get input stream")

        // Download completed successfully
        setState(Constants.STATE_COMPLETED)
        listener?.onDownloadComplete(
            downloadRequestFileModel.downloadId,
            true,
            outputFile.absolutePath
        )
    }

    private suspend fun downloadDataStream(
        inputStream: java.io.InputStream,
        outputStream: FileOutputStream
    ) {
        val buffer = ByteArray(Constants.BUFFER_SIZE)
        var bytesRead: Int
        var lastProgressUpdate = 0L

        while (inputStream.read(buffer).also { bytesRead = it } != -1) {
            // Check for cancellation
            if (isCancelled.get()) {
                throw CancellationException("Download cancelled")
            }

            // Handle pause state
            while (isPaused.get() && !isCancelled.get()) {
                delay(100)
            }

            // Write data to file
            outputStream.write(buffer, 0, bytesRead)
            outputStream.flush()

            // Update progress
            val currentDownloaded = downloadedBytes.addAndGet(bytesRead.toLong())

            // Throttle progress updates (every 100KB or 1%)
            val shouldUpdateProgress = currentDownloaded - lastProgressUpdate >= 100_000 ||
                    (totalBytes.get() > 0 && (currentDownloaded * 100 / totalBytes.get()) > (lastProgressUpdate * 100 / totalBytes.get()))

            if (shouldUpdateProgress) {
                notifyProgress(currentDownloaded)
                lastProgressUpdate = currentDownloaded
            }

            // Yield to allow other coroutines to run and check for state changes
            yield()
        }
    }

    private fun notifyProgress(downloaded: Long) {
        val total = totalBytes.get()
        val percentage = if (total > 0) {
            ((downloaded * 100) / total).toInt()
        } else 0

        listener?.onProgressUpdate(
            downloadRequestFileModel.downloadId,
            downloaded,
            total,
            percentage
        )
    }

    private fun handleNetworkError(exception: IOException) {
        if (networkManager.isConnected()) {
            // Network is available but connection failed - retry
            handleRetry(exception)
        } else {
            // No network - wait for reconnection
            setState(Constants.STATE_WAITING_FOR_NETWORK)
            isWaitingForNetwork.set(true)
            Log.d(TAG, "Network lost during download, waiting for reconnection...")
        }
    }

    private fun handleRetry(exception: Exception) {
        if (retryCount < Constants.MAX_RETRY_ATTEMPTS && !isCancelled.get()) {
            retryCount++
            Log.d(TAG, "Retrying download... Attempt $retryCount/${Constants.MAX_RETRY_ATTEMPTS}")

            scope.launch {
                delay(Constants.RETRY_DELAY_MS)
                if (!isCancelled.get()) {
                    executeDownload()
                }
            }
        } else {
            setState(Constants.STATE_FAILED)
            listener?.onDownloadError(
                downloadRequestFileModel.downloadId,
                "Download failed after $retryCount attempts: ${exception.message}"
            )
        }
    }

    private fun handleDownloadError(exception: Exception) {
        setState(Constants.STATE_FAILED)
        listener?.onDownloadError(
            downloadRequestFileModel.downloadId,
            "Download error: ${exception.message}"
        )
    }

    private fun handleDownloadCancellation() {
        setState(Constants.STATE_CANCELLED)
        // Delete partial file if download was cancelled
        if (outputFile.exists() && downloadedBytes.get() < totalBytes.get()) {
            outputFile.delete()
            Log.d(TAG, "Deleted partial download file")
        }
        listener?.onDownloadCancelled(downloadRequestFileModel.downloadId)
    }

    // DownloadController implementation
    override fun pauseDownload() {
        if (currentState.get() == Constants.STATE_DOWNLOADING) {
            isPaused.set(true)
            setState(Constants.STATE_PAUSED)
            listener?.onDownloadPaused(downloadRequestFileModel.downloadId)
            Log.d(TAG, "Download paused")
        }
    }

    override fun resumeDownload() {
        when (currentState.get()) {
            Constants.STATE_PAUSED -> {
                if (networkManager.isConnected()) {
                    isPaused.set(false)
                    setState(Constants.STATE_DOWNLOADING)
                    listener?.onDownloadResumed(downloadRequestFileModel.downloadId)
                    Log.d(TAG, "Download resumed")
                } else {
                    setState(Constants.STATE_WAITING_FOR_NETWORK)
                    isWaitingForNetwork.set(true)
                    Log.d(TAG, "Cannot resume - no network connection")
                }
            }
            Constants.STATE_FAILED -> {
                // Allow resume after failure
                retryCount = 0
                executeDownload()
            }
        }
    }

    override fun cancelDownload() {
        isCancelled.set(true)
        isPaused.set(false)
        downloadJob?.cancel()
        cleanup()
        Log.d(TAG, "Download cancelled")
    }

    override fun getDownloadState(): Int = currentState.get()

    override fun getCurrentProgress(): Long = downloadedBytes.get()

    override fun getTotalSize(): Long = totalBytes.get()

    // NetworkManager.NetworkConnectionState implementation
    override fun onConnected() {
        Log.d(TAG, "Network reconnected")

        if (isWaitingForNetwork.get() || currentState.get() == Constants.STATE_WAITING_FOR_NETWORK) {
            isWaitingForNetwork.set(false)
            retryCount = 0 // Reset retry count on network reconnection

            listener?.onNetworkReconnected(downloadRequestFileModel.downloadId)

            // Resume download automatically when network is restored
            executeDownload()
        }
    }

    override fun onDisconnected() {
        Log.d(TAG, "Network disconnected")

        if (currentState.get() == Constants.STATE_DOWNLOADING) {
            setState(Constants.STATE_WAITING_FOR_NETWORK)
            isWaitingForNetwork.set(true)
        }
    }

    private fun setState(newState: Int) {
        val oldState = currentState.getAndSet(newState)
        if (oldState != newState) {
            listener?.onDownloadStateChanged(downloadRequestFileModel.downloadId, newState)
        }
    }

    fun cleanup() {
        downloadJob?.cancel()
        httpConnectionManager.disconnect()
        networkManager.cleanup()
        scope.cancel()
    }

    // Utility methods
    fun getDownloadInfo(): DownloadRequestFileModel {
        return downloadRequestFileModel.copy(
            downloadedBytes = downloadedBytes.get(),
            totalBytes = totalBytes.get(),
            state = currentState.get(),
            progress = if (totalBytes.get() > 0) {
                (downloadedBytes.get().toFloat() / totalBytes.get().toFloat()) * 100
            } else 0f
        )
    }

    fun isActive(): Boolean {
        return currentState.get() in listOf(
            Constants.STATE_DOWNLOADING,
            Constants.STATE_WAITING_FOR_NETWORK
        )
    }
}

package com.marusys.downloadsdk

import android.Manifest
import android.content.Context
import android.util.Log
import androidx.annotation.RequiresPermission
import com.marusys.downloadsdk.model.DownloadRequestFileModel
import kotlinx.coroutines.*
import java.io.File
import java.io.FileOutputStream
import java.io.IOException
import java.util.concurrent.atomic.AtomicBoolean
import java.util.concurrent.atomic.AtomicInteger
import java.util.concurrent.atomic.AtomicLong

class DownloadTaskManager(
    private val context: Context,
    private var downloadRequestFileModel: DownloadRequestFileModel,
    private val outputFile: File,
    private val listener: DownloadListener? = null
) : DownloadController, NetworkManager.NetworkConnectionState {

    private val TAG = "DownloadTaskManager"

    // Core components
    private val scope = CoroutineScope(Dispatchers.IO + SupervisorJob())
    private val httpConnectionManager = HttpConnectionManager(downloadRequestFileModel)
    private val networkManager = NetworkManager(context, this)

    // State management
    private val currentState = AtomicInteger(Constants.STATE_IDLE)
    private val isPaused = AtomicBoolean(false)
    private val isCancelled = AtomicBoolean(false)
    private val isWaitingForNetwork = AtomicBoolean(false)

    // Download progress tracking
    private val downloadedBytes = AtomicLong(downloadRequestFileModel.downloadedBytes)
    private val totalBytes = AtomicLong(downloadRequestFileModel.totalBytes)

    // Jobs and retry management
    private var downloadJob: Job? = null
    private var retryCount = 0

    @RequiresPermission(Manifest.permission.ACCESS_NETWORK_STATE)
    fun startDownload() {
        if (currentState.get() == Constants.STATE_DOWNLOADING) {
            Log.w(TAG, "Download already in progress")
            return
        }

        networkManager.registerNetworkCallback()

        if (!networkManager.isConnected()) {
            setState(Constants.STATE_WAITING_FOR_NETWORK)
            Log.d(TAG, "No network connection, waiting for network...")
            return
        }

        executeDownload()
    }

    private fun executeDownload() {
        if (isCancelled.get()) return

        setState(Constants.STATE_DOWNLOADING)
        isPaused.set(false)

        downloadJob = scope.launch {
            try {
                performDownload()
            } catch (e: CancellationException) {
                Log.d(TAG, "Download cancelled")
                handleDownloadCancellation()
            } catch (e: IOException) {
                Log.e(TAG, "Download IO error", e)
                handleNetworkError(e)
            } catch (e: Exception) {
                Log.e(TAG, "Download error", e)
                handleDownloadError(e)
            }
        }
    }

    private suspend fun performDownload() {
        // Get current downloaded bytes (important for resume)
        val currentDownloadedBytes = downloadedBytes.get()

        // Update request model with current progress for HTTP Range header
        downloadRequestFileModel = downloadRequestFileModel.copy(
            downloadedBytes = currentDownloadedBytes
        )

        Log.d(TAG, "Starting download from byte: $currentDownloadedBytes")

        // Establish connection with Range header for resume
        httpConnectionManager.buildConnectionToDownload()

        val contentLength = httpConnectionManager.getContentLength()

        // Set total bytes correctly for resume scenarios
        if (totalBytes.get() <= 0) {
            // For new downloads, total = content length + already downloaded
            val totalSize = if (currentDownloadedBytes > 0) {
                // Resume: add current downloaded bytes to remaining content length
                currentDownloadedBytes + contentLength
            } else {
                // New download: just the content length
                contentLength
            }
            totalBytes.set(totalSize)
            Log.d(TAG, "Total size set to: $totalSize bytes")
        }

        // Validate that we can resume
        if (currentDownloadedBytes > 0 && outputFile.exists()) {
            val actualFileSize = outputFile.length()
            if (actualFileSize != currentDownloadedBytes) {
                Log.w(TAG, "File size mismatch. Expected: $currentDownloadedBytes, Actual: $actualFileSize")
                // Reset to actual file size
                downloadedBytes.set(actualFileSize)
                downloadRequestFileModel = downloadRequestFileModel.copy(
                    downloadedBytes = actualFileSize
                )
                // Reconnect with correct range
                httpConnectionManager.disconnect()
                httpConnectionManager.buildConnectionToDownload()
            }
        }

        // Start streaming download from current position
        httpConnectionManager.getInputStream()?.use { inputStream ->
            // IMPORTANT: Use append mode (true) when resuming
            FileOutputStream(outputFile, currentDownloadedBytes > 0).use { outputStream ->
                Log.d(TAG, "Resume mode: ${currentDownloadedBytes > 0}, File exists: ${outputFile.exists()}")
                downloadDataStream(inputStream, outputStream)
            }
        } ?: throw IOException("Unable to get input stream")

        // Download completed successfully
        setState(Constants.STATE_COMPLETED)
        listener?.onDownloadComplete(
            downloadRequestFileModel.downloadId,
            true,
            outputFile.absolutePath
        )

        Log.d(TAG, "Download completed. Final size: ${outputFile.length()} bytes")
    }

    private suspend fun downloadDataStream(
        inputStream: java.io.InputStream,
        outputStream: FileOutputStream
    ) {
        val buffer = ByteArray(Constants.BUFFER_SIZE)
        var bytesRead: Int
        var lastProgressUpdate = 0L

        while (inputStream.read(buffer).also { bytesRead = it } != -1) {
            // Check for cancellation
            if (isCancelled.get()) {
                throw CancellationException("Download cancelled")
            }

            // Handle pause state
            while (isPaused.get() && !isCancelled.get()) {
                delay(100)
            }

            // Write data to file
            outputStream.write(buffer, 0, bytesRead)
            outputStream.flush()

            // Update progress
            val currentDownloaded = downloadedBytes.addAndGet(bytesRead.toLong())

            // Throttle progress updates (every 100KB or 1%)
            val shouldUpdateProgress = currentDownloaded - lastProgressUpdate >= 100_000 ||
                    (totalBytes.get() > 0 && (currentDownloaded * 100 / totalBytes.get()) > (lastProgressUpdate * 100 / totalBytes.get()))

            if (shouldUpdateProgress) {
                notifyProgress(currentDownloaded)
                lastProgressUpdate = currentDownloaded
            }

            // Yield to allow other coroutines to run and check for state changes
            yield()
        }
    }

    private fun notifyProgress(downloaded: Long) {
        val total = totalBytes.get()
        val percentage = if (total > 0) {
            ((downloaded * 100) / total).toInt()
        } else 0

        listener?.onProgressUpdate(
            downloadRequestFileModel.downloadId,
            downloaded,
            total,
            percentage
        )
    }

    private fun handleNetworkError(exception: IOException) {
        if (networkManager.isConnected()) {
            // Network is available but connection failed - retry
            handleRetry(exception)
        } else {
            // No network - wait for reconnection
            setState(Constants.STATE_WAITING_FOR_NETWORK)
            isWaitingForNetwork.set(true)
            Log.d(TAG, "Network lost during download, waiting for reconnection...")
        }
    }

    private fun handleRetry(exception: Exception) {
        if (retryCount < Constants.MAX_RETRY_ATTEMPTS && !isCancelled.get()) {
            retryCount++
            Log.d(TAG, "Retrying download... Attempt $retryCount/${Constants.MAX_RETRY_ATTEMPTS}")

            scope.launch {
                delay(Constants.RETRY_DELAY_MS)
                if (!isCancelled.get()) {
                    executeDownload()
                }
            }
        } else {
            setState(Constants.STATE_FAILED)
            listener?.onDownloadError(
                downloadRequestFileModel.downloadId,
                "Download failed after $retryCount attempts: ${exception.message}"
            )
        }
    }

    private fun handleDownloadError(exception: Exception) {
        setState(Constants.STATE_FAILED)
        listener?.onDownloadError(
            downloadRequestFileModel.downloadId,
            "Download error: ${exception.message}"
        )
    }

    private fun handleDownloadCancellation() {
        setState(Constants.STATE_CANCELLED)
        // Delete partial file if download was cancelled
        if (outputFile.exists() && downloadedBytes.get() < totalBytes.get()) {
            outputFile.delete()
            Log.d(TAG, "Deleted partial download file")
        }
        listener?.onDownloadCancelled(downloadRequestFileModel.downloadId)
    }

    // DownloadController implementation
    override fun pauseDownload() {
        if (currentState.get() == Constants.STATE_DOWNLOADING) {
            isPaused.set(true)
            setState(Constants.STATE_PAUSED)
            listener?.onDownloadPaused(downloadRequestFileModel.downloadId)
            Log.d(TAG, "Download paused")
        }
    }

    override fun resumeDownload() {
        when (currentState.get()) {
            Constants.STATE_PAUSED -> {
                if (networkManager.isConnected()) {
                    isPaused.set(false)
                    setState(Constants.STATE_DOWNLOADING)
                    listener?.onDownloadResumed(downloadRequestFileModel.downloadId)
                    Log.d(TAG, "Download resumed")
                } else {
                    setState(Constants.STATE_WAITING_FOR_NETWORK)
                    isWaitingForNetwork.set(true)
                    Log.d(TAG, "Cannot resume - no network connection")
                }
            }
            Constants.STATE_FAILED -> {
                // Allow resume after failure
                retryCount = 0
                executeDownload()
            }
        }
    }

    override fun cancelDownload() {
        isCancelled.set(true)
        isPaused.set(false)
        downloadJob?.cancel()
        cleanup()
        Log.d(TAG, "Download cancelled")
    }

    override fun getDownloadState(): Int = currentState.get()

    override fun getCurrentProgress(): Long = downloadedBytes.get()

    override fun getTotalSize(): Long = totalBytes.get()

    // NetworkManager.NetworkConnectionState implementation
    override fun onConnected() {
        Log.d(TAG, "Network reconnected")

        if (isWaitingForNetwork.get() || currentState.get() == Constants.STATE_WAITING_FOR_NETWORK) {
            isWaitingForNetwork.set(false)
            retryCount = 0 // Reset retry count on network reconnection

            listener?.onNetworkReconnected(downloadRequestFileModel.downloadId)

            // Resume download automatically when network is restored
            executeDownload()
        }
    }

    override fun onDisconnected() {
        Log.d(TAG, "Network disconnected")

        if (currentState.get() == Constants.STATE_DOWNLOADING) {
            setState(Constants.STATE_WAITING_FOR_NETWORK)
            isWaitingForNetwork.set(true)
        }
    }

    private fun setState(newState: Int) {
        val oldState = currentState.getAndSet(newState)
        if (oldState != newState) {
            listener?.onDownloadStateChanged(downloadRequestFileModel.downloadId, newState)
        }
    }

    fun cleanup() {
        downloadJob?.cancel()
        httpConnectionManager.disconnect()
        networkManager.cleanup()
        scope.cancel()
    }

    // Utility methods
    fun getDownloadInfo(): DownloadRequestFileModel {
        return downloadRequestFileModel.copy(
            downloadedBytes = downloadedBytes.get(),
            totalBytes = totalBytes.get(),
            state = currentState.get(),
            progress = if (totalBytes.get() > 0) {
                (downloadedBytes.get().toFloat() / totalBytes.get().toFloat()) * 100
            } else 0f
        )
    }

    fun isActive(): Boolean {
        return currentState.get() in listOf(
            Constants.STATE_DOWNLOADING,
            Constants.STATE_WAITING_FOR_NETWORK
        )
    }
}

package com.marusys.downloadsdk

import android.annotation.SuppressLint
import android.util.Log
import com.marusys.downloadsdk.Constants.CONNECT_TIMEOUT
import com.marusys.downloadsdk.model.DownloadRequestFileModel
import java.io.InputStream
import java.net.HttpURLConnection
import java.net.URL
import java.util.Locale
import java.util.concurrent.TimeUnit

class HttpConnectionManager(private val request: DownloadRequestFileModel) {
    private val TAG = "HttpConnectionManager"
    private var httpURLConnection : HttpURLConnection? = null

    suspend fun buildConnectionToDownload() {
        disconnect()
        httpURLConnection = createConnection(request.url).also { conn ->
            configureConnection(conn)
            conn.connect()
        }
    }

    private fun createConnection(url: String): HttpURLConnection {
        return URL(url).openConnection() as HttpURLConnection
    }
    private fun configureConnection(conn: HttpURLConnection) {
        conn.connectTimeout = CONNECT_TIMEOUT.toInt()
        if (request.downloadedBytes > 0) {
            val rangeHeader = String.format(
                Locale.ENGLISH,
                "bytes=%d-",
                request.downloadedBytes
            )
            Log.d(TAG, "configureConnection: =====> rangeHeader")
            conn.setRequestProperty(Constants.RANGE_HEADER, rangeHeader)
        }
    }
    fun getInputStream(): InputStream? = httpURLConnection?.inputStream
    fun getResponseCode(): Int = httpURLConnection?.responseCode ?: 0

    fun getContentLength(): Long = httpURLConnection?.contentLengthLong ?: -1L

    fun isValidResponse(): Boolean {
        val responseCode = getResponseCode()
        return responseCode == Constants.HTTP_OK || responseCode == Constants.HTTP_PARTIAL_CONTENT
    }
    fun disconnect() {
        httpURLConnection?.disconnect()
        httpURLConnection = null
    }
}

package com.marusys.downloadsdk

import android.util.Log
import com.marusys.downloadsdk.Constants.CONNECT_TIMEOUT
import com.marusys.downloadsdk.model.DownloadRequestFileModel
import java.io.IOException
import java.io.InputStream
import java.net.HttpURLConnection
import java.net.URL
import java.util.Locale
import java.util.concurrent.TimeUnit

class HttpConnectionManager(private val request: DownloadRequestFileModel) {
    private val TAG = "HttpConnectionManager"
    private var httpURLConnection : HttpURLConnection? = null

    suspend fun buildConnectionToDownload() {
        disconnect()
        httpURLConnection = createConnection(request.url).also { conn ->
            configureConnection(conn)
            conn.connect()
        }

        if (!isValidResponse()) {
            throw IOException("Invalid HTTP response: ${getResponseCode()}")
        }
    }

    private fun createConnection(url: String): HttpURLConnection {
        return URL(url).openConnection() as HttpURLConnection
    }

    private fun configureConnection(conn: HttpURLConnection) {
        conn.connectTimeout = CONNECT_TIMEOUT.toInt()
        conn.readTimeout = CONNECT_TIMEOUT.toInt()

        if (request.downloadedBytes > 0) {
            val rangeHeader = String.format(
                Locale.ENGLISH,
                "bytes=%d-",
                request.downloadedBytes
            )
            Log.d(TAG, "configureConnection: =====> rangeHeader: $rangeHeader")
            conn.setRequestProperty(Constants.RANGE_HEADER, rangeHeader)
        }
    }

    fun getInputStream(): InputStream? = httpURLConnection?.inputStream

    fun getResponseCode(): Int = httpURLConnection?.responseCode ?: 0

    fun getContentLength(): Long = httpURLConnection?.contentLengthLong ?: -1L

    fun getETag(): String? = httpURLConnection?.getHeaderField(Constants.ETAG_HEADER)

    fun isValidResponse(): Boolean {
        val responseCode = getResponseCode()
        return responseCode == Constants.HTTP_OK || responseCode == Constants.HTTP_PARTIAL_CONTENT
    }

    fun disconnect() {
        httpURLConnection?.disconnect()
        httpURLConnection = null
    }
}

package com.marusys.downloadsdk.db

import androidx.room.Dao
import androidx.room.Insert
import androidx.room.OnConflictStrategy.Companion.REPLACE
import androidx.room.Query

@Dao
interface DataRequestDao {
    @Insert(onConflict = REPLACE)
    suspend fun insertDownloadRequest(dataRequest: DataRequestModel)

    @Update
    suspend fun updateDownloadRequest(dataRequest: DataRequestModel)

    @Query("UPDATE download_requests SET state = :state, updatedAt = :updatedAt WHERE downloadId = :downloadId")
    suspend fun updateDownloadState(downloadId: Long, state: Int, updatedAt: Long = System.currentTimeMillis())

    @Query("UPDATE download_requests SET downloadedBytes = :downloadedBytes, progress = :progress, updatedAt = :updatedAt WHERE downloadId = :downloadId")
    suspend fun updateDownloadProgress(downloadId: Long, downloadedBytes: Long, progress: Float, updatedAt: Long = System.currentTimeMillis())

    @Query("UPDATE download_requests SET totalBytes = :totalBytes, updatedAt = :updatedAt WHERE downloadId = :downloadId")
    suspend fun updateTotalBytes(downloadId: Long, totalBytes: Long, updatedAt: Long = System.currentTimeMillis())

    @Query("UPDATE download_requests SET retryCount = :retryCount, updatedAt = :updatedAt WHERE downloadId = :downloadId")
    suspend fun updateRetryCount(downloadId: Long, retryCount: Int, updatedAt: Long = System.currentTimeMillis())

    @Query("UPDATE download_requests SET errorMessage = :errorMessage, updatedAt = :updatedAt WHERE downloadId = :downloadId")
    suspend fun updateErrorMessage(downloadId: Long, errorMessage: String?, updatedAt: Long = System.currentTimeMillis())

    // Query methods for resume functionality
    @Query("SELECT * FROM download_requests WHERE downloadId = :downloadId")
    suspend fun getDownloadRequest(downloadId: Long): DataRequestModel?

    @Query("SELECT * FROM download_requests WHERE state IN (:states)")
    suspend fun getDownloadsByState(states: List<Int>): List<DataRequestModel>

    @Query("SELECT * FROM download_requests WHERE state = :state")
    suspend fun getDownloadsByState(state: Int): List<DataRequestModel>

    // Get incomplete downloads for resume (downloading, paused, waiting for network)
    @Query("SELECT * FROM download_requests WHERE state IN (1, 2, 6) ORDER BY updatedAt DESC")
    suspend fun getIncompleteDownloads(): List<DataRequestModel>

    // Get failed downloads for retry
    @Query("SELECT * FROM download_requests WHERE state = 4 ORDER BY updatedAt DESC")
    suspend fun getFailedDownloads(): List<DataRequestModel>

    // Get all downloads ordered by most recent
    @Query("SELECT * FROM download_requests ORDER BY updatedAt DESC")
    suspend fun getAllDownloads(): List<DataRequestModel>

    // Live data for UI updates
    @Query("SELECT * FROM download_requests ORDER BY updatedAt DESC")
    fun getAllDownloadsFlow(): Flow<List<DataRequestModel>>

    @Query("SELECT * FROM download_requests WHERE downloadId = :downloadId")
    fun getDownloadRequestFlow(downloadId: Long): Flow<DataRequestModel?>

    // Cleanup methods
    @Query("DELETE FROM download_requests WHERE downloadId = :downloadId")
    suspend fun deleteDownloadRequest(downloadId: Long)

    @Query("DELETE FROM download_requests WHERE state = :state")
    suspend fun deleteDownloadsByState(state: Int)

    // Delete old completed downloads (older than specified timestamp)
    @Query("DELETE FROM download_requests WHERE state = 3 AND updatedAt < :beforeTimestamp")
    suspend fun deleteOldCompletedDownloads(beforeTimestamp: Long)

    // Get download statistics
    @Query("SELECT COUNT(*) FROM download_requests WHERE state = :state")
    suspend fun getDownloadCountByState(state: Int): Int

    @Query("SELECT state, COUNT(*) as count FROM download_requests GROUP BY state")
    suspend fun getDownloadStatistics(): Map<Int, Int>
}

