package com.marusys.upgradefirmware

import java.io.File
import kotlinx.coroutines.*

/**
 * Example usage of the download functionality with pause, resume, and cancel operations
 */
class DownloadExample {

    fun demonstrateDownloadOperations() {
        val firmwareManager = FirmwareManager.getInstance()

        // Create a download request
        val downloadRequest = DownloadRequest(
            url = "https://example.com/firmware.bin",
            downloadId = System.currentTimeMillis(),
            fileName = "firmware.bin",
            connectTimeOut = 30000,
            readTimeOut = 30000,
            downloadedBytes = 0L,
            requestState = RequestState.None
        )

        // Specify output file
        val outputFile = File("/path/to/download/firmware.bin")

        // Start download
        firmwareManager.download(downloadRequest, outputFile)

        // Example of pausing after 5 seconds
        CoroutineScope(Dispatchers.Main).launch {
            delay(5000)
            println("Pausing download...")
            firmwareManager.pause(downloadRequest)

            // Resume after 3 seconds
            delay(3000)
            println("Resuming download...")
            firmwareManager.resume(downloadRequest)

            // Cancel after 10 more seconds
            delay(10000)
            println("Cancelling download...")
            firmwareManager.cancelRequest(downloadRequest)
        }
    }

    fun monitorDownloadProgress(downloadId: Long) {
        val firmwareManager = FirmwareManager.getInstance()

        CoroutineScope(Dispatchers.IO).launch {
            while (true) {
                val state = firmwareManager.getRequestState(downloadId)
                val progress = firmwareManager.getDownloadProgress(downloadId)
                val isActive = firmwareManager.isDownloadActive(downloadId)

                println("Download ID: $downloadId")
                println("State: $state")
                println("Progress: $progress bytes")
                println("Active: $isActive")
                println("---")

                // Exit monitoring if download is completed, cancelled, or failed
                if (state in listOf(RequestState.Completed, RequestState.Cancelled, RequestState.Failed)) {
                    break
                }

                delay(1000) // Check every second
            }
        }
    }

    fun handleDownloadWithCallbacks() {
        val downloadTask = DownloadTask()

        // Set up progress callback
        downloadTask.onProgressUpdate = { downloaded, total, percentage ->
            println("Progress: $downloaded/$total bytes ($percentage%)")
        }

        // Set up completion callback
        downloadTask.onDownloadComplete = { success, filePath ->
            if (success) {
                println("Download completed successfully: $filePath")
            } else {
                println("Download failed")
            }
        }

        // Set up error callback
        downloadTask.onDownloadError = { error ->
            println("Download error: $error")
        }

        // Example of manual control
        CoroutineScope(Dispatchers.Main).launch {
            delay(5000)
            if (downloadTask.isDownloadActive()) {
                downloadTask.pauseDownload()
                println("Download paused")

                delay(2000)
                downloadTask.resumeDownload()
                println("Download resumed")
            }
        }
    }
}

package com.marusys.upgradefirmware

import java.io.File
import kotlinx.coroutines.*

/**
 * Clean usage examples demonstrating the refactored download functionality
 * Shows proper implementation of the listener pattern and clean API usage
 */
class CleanDownloadExample {

    fun demonstrateBasicDownload() {
        val firmwareManager = FirmwareManager.getInstance()

        // Create download request using builder pattern for better readability
        val downloadRequest = DownloadRequest.Builder()
            .url("https://example.com/firmware.bin")
            .downloadId(System.currentTimeMillis())
            .fileName("firmware.bin")
            .connectTimeOut(30_000)
            .readTimeOut(30_000)
            .build()

        val outputFile = File("/path/to/download/firmware.bin")

        // Create listener for handling download events
        val downloadListener = object : DownloadListener {
            override fun onProgressUpdate(downloaded: Long, total: Long, percentage: Int) {
                println("Download progress: $percentage% ($downloaded/$total bytes)")
            }

            override fun onDownloadComplete(success: Boolean, filePath: String?) {
                if (success) {
                    println("Download completed successfully: $filePath")
                } else {
                    println("Download failed")
                }
            }

            override fun onDownloadError(error: String) {
                println("Download error: $error")
            }

            override fun onDownloadStateChanged(state: DownloadState) {
                println("Download state changed to: $state")
            }
        }

        // Start download with proper error handling
        try {
            val downloadId = firmwareManager.startDownload(downloadRequest, outputFile, downloadListener)
            println("Download started with ID: $downloadId")
        } catch (e: IllegalStateException) {
            println("Failed to start download: ${e.message}")
        }
    }

    fun demonstrateDownloadControl() {
        val firmwareManager = FirmwareManager.getInstance()
        val downloadId = System.currentTimeMillis()

        CoroutineScope(Dispatchers.Main).launch {
            // Pause download after 5 seconds
            delay(5_000)
            if (firmwareManager.pauseDownload(downloadId)) {
                println("Download paused successfully")
            } else {
                println("Failed to pause download - download not found or not active")
            }

            // Resume after 3 seconds
            delay(3_000)
            if (firmwareManager.resumeDownload(downloadId)) {
                println("Download resumed successfully")
            }

            // Cancel after 10 more seconds
            delay(10_000)
            if (firmwareManager.cancelDownload(downloadId)) {
                println("Download cancelled successfully")
            }
        }
    }

    fun demonstrateMultipleDownloads() {
        val firmwareManager = FirmwareManager.getInstance()

        // Start multiple downloads
        val downloadIds = mutableListOf<Long>()

        repeat(3) { index ->
            val request = DownloadRequest.Builder()
                .url("https://example.com/firmware$index.bin")
                .downloadId(System.currentTimeMillis() + index)
                .fileName("firmware$index.bin")
                .build()

            val outputFile = File("/path/to/firmware$index.bin")
            val downloadId = firmwareManager.startDownload(request, outputFile)
            downloadIds.add(downloadId)
        }

        // Monitor all downloads
        monitorDownloads(downloadIds)

        // Cancel all downloads after some time
        CoroutineScope(Dispatchers.IO).launch {
            delay(30_000)
            firmwareManager.cancelAllDownloads()
            println("All downloads cancelled")
        }
    }

    private fun monitorDownloads(downloadIds: List<Long>) {
        val firmwareManager = FirmwareManager.getInstance()

        CoroutineScope(Dispatchers.IO).launch {
            while (downloadIds.any { firmwareManager.isDownloadActive(it) }) {
                downloadIds.forEach { downloadId ->
                    val state = firmwareManager.getDownloadState(downloadId)
                    val progress = firmwareManager.getDownloadProgress(downloadId)
                    val isActive = firmwareManager.isDownloadActive(downloadId)

                    println("Download $downloadId - State: $state, Progress: $progress bytes, Active: $isActive")
                }

                println("Active downloads: ${firmwareManager.getActiveDownloadCount()}")
                println("---")

                delay(2_000) // Check every 2 seconds
            }

            println("All downloads completed or stopped")
        }
    }

    fun demonstrateCustomDownloadListener() {
        class CustomDownloadListener(private val downloadName: String) : DownloadListener {
            private var lastProgressTime = System.currentTimeMillis()

            override fun onProgressUpdate(downloaded: Long, total: Long, percentage: Int) {
                val currentTime = System.currentTimeMillis()
                if (currentTime - lastProgressTime >= 1000) { // Throttle updates to once per second
                    println("[$downloadName] Progress: $percentage% - ${formatBytes(downloaded)}/${formatBytes(total)}")
                    lastProgressTime = currentTime
                }
            }

            override fun onDownloadComplete(success: Boolean, filePath: String?) {
                if (success) {
                    println("[$downloadName]  Download completed: $filePath")
                } else {
                    println("[$downloadName]  Download failed")
                }
            }

            override fun onDownloadError(error: String) {
                println("[$downloadName]  Error: $error")
            }

            override fun onDownloadStateChanged(state: DownloadState) {
                val emoji = when (state) {
                    DownloadState.CONNECTING -> ""
                    DownloadState.DOWNLOADING -> ""
                    DownloadState.PAUSED -> ""
                    DownloadState.COMPLETED -> ""
                    DownloadState.CANCELLED -> ""
                    DownloadState.FAILED -> ""
                    else -> ""
                }
                println("[$downloadName] $emoji State changed to: $state")
            }

            private fun formatBytes(bytes: Long): String {
                return when {
                    bytes >= 1024 * 1024 -> "${bytes / (1024 * 1024)} MB"
                    bytes >= 1024 -> "${bytes / 1024} KB"
                    else -> "$bytes B"
                }
            }
        }

        val firmwareManager = FirmwareManager.getInstance()
        val customListener = CustomDownloadListener("Firmware Update")

        val request = DownloadRequest.Builder()
            .url("https://example.com/large-firmware.bin")
            .downloadId(System.currentTimeMillis())
            .fileName("large-firmware.bin")
            .build()

        val outputFile = File("/path/to/large-firmware.bin")
        firmwareManager.startDownload(request, outputFile, customListener)
    }
}

package com.marusys.upgradefirmware.database

import androidx.room.Database
